    method: "frappe.client.get",
    args: {
        doctype: "Employee",
        filters: {
            user_id: ['=', frappe.session.user]
        },
        fields: ["custom_supplier", "branch"],
    },
    callback(employeeResponse) {
        if (employeeResponse && employeeResponse.message) {
            const employee = employeeResponse.message;

            // Exit if branch is missing
            if (!employee.branch) {
                return;
            }

            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Supplier",
                    filters: {
                        name: employee.custom_supplier
                    },
                    fields: ["custom_show_capex_budget", "custom_show_opex_budget", "supplier_name"],
                },
                callback(supplierResponse) {
                    if (supplierResponse && supplierResponse.message) {
                        const supplier = supplierResponse.message;

                        frappe.call({
                            method: "frappe.client.get",
                            args: {
                                doctype: "Branch",
                                filters: {
                                    name: employee.branch
                                },
                                fields: ["branch", "custom_capex_budget", "custom_opex_budget", "custom_branch_code", "custom_supplier"],
                            },
                            callback(branchResponse) {
                                if (branchResponse && branchResponse.message) {
                                    const branch = branchResponse.message;

                                    // Count sub-branches linked to this branch
                                    frappe.call({
                                        method: "frappe.client.get_list",
                                        args: {
                                            doctype: "Sub Branch",
                                            filters: {
                                                branch: employee.branch
                                            },
                                            fields: ["name"],
                                        },
                                        callback(subBranchResponse) {
                                            // Handle Capex display
                                            const capexSection = root_element.querySelector('#capex_section');
                                            const capexElement = root_element.querySelector('#custom_capex_budget');

                                            if (supplier.custom_show_capex_budget) {
                                                if (capexSection) capexSection.style.display = 'block';
                                                if (capexElement) {
                                                    // Check if value exists before formatting
                                                    const capexValue = branch.custom_capex_budget;
                                                    if (capexValue && capexValue !== "N/A") {
                                                        capexElement.textContent = "₹ " + capexValue;
                                                    } else {
                                                        capexElement.textContent = "N/A";
                                                    }
                                                }
                                            } else {
                                                if (capexSection) capexSection.style.display = 'none';
                                            }

                                            // Handle Opex display
                                            const opexSection = root_element.querySelector('#opex_section');
                                            const opexElement = root_element.querySelector('#custom_opex_budget');

                                            if (supplier.custom_show_opex_budget) {
                                                if (opexSection) opexSection.style.display = 'block';
                                                if (opexElement) {
                                                    // Check if value exists before formatting
                                                    const opexValue = branch.custom_opex_budget;
                                                    if (opexValue && opexValue !== "N/A") {
                                                        opexElement.textContent = "₹ " + opexValue;
                                                    } else {
                                                        opexElement.textContent = "N/A";
                                                    }
                                                }
                                            } else {
                                                if (opexSection) opexSection.style.display = 'none';
                                            }

                                            // Only if both budget displays should be visible, then add branch info
                                            if (supplier.custom_show_capex_budget || supplier.custom_show_opex_budget) {
                                                // Get sub-branch count
                                                const subBranchCount = subBranchResponse && subBranchResponse.message ? subBranchResponse.message.length : 0;

                                                // Add branch name, supplier display and sub-branch count
                                                const infoContainer = root_element.querySelector('#branch_info');
                                                if (infoContainer) {
                                                    infoContainer.innerHTML = `
                                                        <div class="branch-details">
                                                            <span>Branch: <strong>${branch.branch}</strong> (Code: ${branch.custom_branch_code || 'N/A'})</span>
                                                            <span>Supplier: <strong>${supplier.supplier_name || branch.custom_supplier || 'N/A'}</strong></span>
                                                            <span>Sub-branches: <strong class="sub-branch-count">${subBranchCount}</strong></span>
                                                        </div>
                                                    `;
                                                }

                                                // Make branch info container visible
                                                if (infoContainer) infoContainer.style.display = 'block';

                                                // Make budget container visible
                                                const container = root_element.querySelector('#budget_container');
                                                if (container) container.style.display = 'flex';
                                            } else {
                                                // Hide entire container if both budgets are hidden
                                                const container = root_element.querySelector('#budget_container');
                                                if (container) container.style.display = 'none';

                                                // Also hide the branch info
                                                const infoContainer = root_element.querySelector('#branch_info');
                                                if (infoContainer) infoContainer.style.display = 'none';
                                            }
                                        }
                                    });
                                }
                            }
                        });
                    }
                }
            });
        }
    }
});

