css:
body,
html {
    padding: 10px 0px;
    letter-spacing: 0.3px;
    font-family: "Roboto", sans-serif;
    max-width: 800px;
    margin: auto;
}


.invoice-table {
    width: 100%;
    /* Ensure both tables are half the container's width */
    border: 1px solid #ccc;
    border-collapse: collapse;
}

.invoice-table td {
    border: 1px solid #ccc;
    padding: 10px;
    vertical-align: top;
    font-size: 12px;
}

.logo {
    width: 20%;
    text-align: center;
    vertical-align: top;
}

.company-info {
    width: 80%;
    text-align: left;
    font-size: 12px;
    line-height: 1.5;
}

.company-info strong {
    font-size: 14px;
    display: block;
    margin-bottom: 5px;
}

.buyer-info {
    text-align: left;
    font-size: 12px;
    line-height: 1.5;
}

.section-title {
    font-weight: bold;
    text-align: left;
    padding-left: 10px;
}

.print-format th {
    background-color: transparent !important;
}


/* 2nd Table Css starts here */

table {
    width: 100%;
    border-collapse: collapse;
    margin: 5px 0 0 0;
    font-family: sans-serif;
    /* min-width: 400px; */
}

table thead tr {
    text-align: left;
}

table th {
    font-weight: 700;
    text-align: center;
}

.print-format th {
    background-color: transparent !important;
}

table td {
    font-weight: 400;
}


.header__invoice__table .border-none {
    border: none;
    vertical-align: top;
    padding: 8px 0px;
}

table th,
table td {
    padding: 8px 10px;
    border: 1px solid #dddddd;
    font-family: "Roboto", sans-serif;
    font-size: 12px;
}

/* Style for the bold text in the footer */
.bold {
    font-weight: bold;
}

.header__invoice__table {
    border-collapse: separate;
}

/* 2nd Table Css ends here */


/* Amount Table Component starts here */
.amount__table {
    width: 100%;
    border-collapse: collapse;
    margin: 0px 0;
    font-size: 16px;
    line-height: 1.5;
}

.amount__table td {
    border: 1px solid #c7c7c7;
    border-top: none;
    padding: 8px;
    vertical-align: top;
}

.amount-chargeable {
    text-align: left;
    font-weight: bold;
}

.eoe {
    text-align: right;
}

.amount-words {
    font-weight: bold;
}

.details div,
.amounts div {
    margin-bottom: 8px;
}

.net-amt {
    font-weight: bold;
}

.formula p {
    margin: 0;
}

.company-info p {
    margin: 0;
    line-height: 1.5;
}

/* Amount Table component ends here */

/* Bank Details Component starts here */

.bank-details-table {
    width: 100%;
    border-collapse: collapse;
    margin: 0;
}

.bank-details-title {
    font-weight: bold;
    /* padding: 10px; */
    border: 1px solid #c7c7c7;
    border-top: none;
    text-align: left;
}

.bank-details-info,
.signatory {
    vertical-align: top;
    padding: 10px;
    border: 1px solid #c7c7c7;
    width: 50%;
}

.bank-details-info p,
.signatory p {
    margin: 5px 0;
}

.signatory {
    text-align: center;
}

.signature {
    display: block;
    margin: 10px auto;
    /* height: 50px; */
    width: 120px !important;
}

.authorized-signatory {
    font-weight: bold;
}

.footer-text {
    text-align: center;
    font-size: 12px;
    padding: 10px;
    border: 1px solid #c7c7c7;
    font-style: italic;
}

/* Bank Details Component ends here */

/* GST Component starts here */
.gst-summary {
    width: 100%;
    border-collapse: collapse;
    margin: 0;
}

.gst-summary th,
.gst-summary td {
    border: 1px solid #c7c7c7;
    padding: 8px;
    text-align: left;
}

.gst-summary th.summary-header {
    text-align: left;
    font-weight: bold;
    border-right: none;
}

.gst-summary thead th {
    border-bottom: 1px solid #c7c7c7;
}

.gst-summary tbody tr td:first-child {
    border-left: 1px solid #c7c7c7 !important;
}


/* GST Component ends here */

