        body,
        html {
            padding: 10px 0px;
            letter-spacing: 0.3px;
            max-width: 1000px;
            margin: 0 auto;
            font-family: "Roboto", sans-serif;
        }

        .header__challan {
            display: grid;
            grid-template-columns: 0.3fr 3fr;
            gap: 20px;
        }

        .header__challan>div {
            height: auto;
            padding: 2px 7px;
        }

        .header__challan>div:nth-child(1) {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
        }

        .header__challan>div>img {
            max-width: 120px;
        }

        .header__challan>div>h1 {
            font-size: 32px;
            font-weight: 800;
        }

        .header__challan>div:nth-child(2) {
            text-align: center;
            padding: 0px 20px;
        }

        .header__challan>div:nth-child(2)>p:nth-child(2) {
            margin: 10px 0px 10px 0px;
        }

        .header__challan>div:nth-child(2)>h2 {
            font-size: 26px;
            font-weight: 800;
            margin-top: 10px;
        }

        /* Address css style starts here */
        .address-container-row {
            display: grid;
            grid-template-columns: 2fr 1.5fr;
            gap: 20px;
        }

        .address-container-row>div {
            /* border: 1px solid black; */
            margin-top: 10px;
            height: auto;
            padding: 20px 10px;
        }

        .address-container-row>div:nth-child(1)>h3,
        .address-container-row>div:nth-child(1)>p:nth-child(2) {
            margin-top: 0px !important;
            font-weight: 800;
            font-size: 16px;
            margin-bottom: 3px;
        }

        .address-container-row>div>p {
            font-weight: 400;
            margin-bottom: 3px;
            font-size: 13px;
        }

        /* Address css style ends here */

        /* Invoice Table css style starts here */
        .invoice-table {
            width: 100%;
            border-collapse: collapse;
            margin: 20px 0;
        }

        .invoice-table th,
        .invoice-table td {
            border: 1px solid #c7c7c7;
            padding: 8px;
            text-align: left;
            color: #1c1c1c;
        }

        .invoice-table th {
            background-color: transparent;
            font-weight: bold;
            font-family: "Roboto", sans-serif;
            font-size: 14px;
        }

        .invoice-table td {
            text-align: left;
            font-size: 13px;
        }

        .invoice-table tbody tr td {
            height: 30px;
        }

        /* Set the width of the "Sr. No." and "Qty" columns */
        .invoice-table .small-column {
            width: 10%;
            text-align: center;
        }

        /* Ensure the "Description" column takes up the remaining space */
        .invoice-table th:not(.small-column),
        .invoice-table td:not(.small-column) {
            width: auto;
        }

        .invoice-table tr td:nth-child(1),
        .invoice-table tr td:last-child {
            text-align: center;
        }

        .print-format th {
            background-color: transparent !important;
        }

        /* Invoice Table css style ends here */

/*css for pdf*/
@media print{
    .payment_qr img {
        width:81px !important;
        height: 81px;
    }
}

.payment_qr img {
    width:81px !important;
    height: 81px;
}
.payment_qr{
    float:left;
}


/*general css*/  
b{
    color : initial;
}

p{
    font-size : 12px !important;
    word-break:break-all;
    margin:0px !important;
}

table{
    width:100%;
}


/*section after heading of print format*/
div[data-label="First Section"] > div:nth-child(1){
    width:40% !important;
    padding-right:0px !important;
    padding-top:20px !important;
}
div[data-label="First Section"] > div:nth-child(2) {
    width:34% !important;
    padding-right:0px !important;
    padding-left:6px !important;
    padding-top:20px !important;
}
div[data-label="First Section"] > div:nth-child(3) {
    width:26% !important;
    padding-left:0px !important;
}


/*css for place of supply*/
div[data-fieldname="place_of_supply"]{
    padding-top:5px !important;
    padding-bottom:5px !important;
    margin-top:0px !important;
    margin-bottom:0px !important;
}
div[data-fieldname="place_of_supply"] > div > label{
    margin-bottom:0px !important;
}
div[data-fieldname="place_of_supply"] > div:nth-child(1){
    width:50% !important;
    line-height: 1;
}
div[data-fieldname="place_of_supply"] > div:nth-child(2){
    width:50% !important;
    line-height: 1;
}


/*css for posting date*/
div[data-fieldname="posting_date"]{
    margin-top:0px;
    margin-bottom:0px;
}
div[data-fieldname="posting_date"] > div:nth-child(1){
    width:50% !important;
    line-height:1.6;
}
div[data-fieldname="posting_date"] > div:nth-child(2){
    width:50% !important;
    line-height:1.6;
}


/*css for first section after heading second column*/
.first_section_second_column{
    padding-bottom:5px !important;
    padding-top:5px !important;
}


/*css for first section after heading third column - qr code column*/
.paid_unpaid{
    padding-bottom:5px !important;
    text-align:end;
}
.paid_unpaid > p{
    font-size:120% !important;
    line-height:1;
}


/*css for irn*/
.print-format p.field-property{
    padding-top:5px !important;
    padding-bottom:5px !important;
}

/*css for company-details*/
table.company-details {
    width:66%;
    display: inline-block;
}

table.company-details tbody {
    display: table;
    width:100%;
}

table.company-details:nth-child(2) {
    width: 32%;
}

/*css for due_date adn paid amount*/
div[data-fieldname="due_date"] > div{
    width:50%;
}
div[data-fieldname="due_date"] > div > label {
    margin:0px ;
}
div[data-fieldname="due_date"] > div:nth-child(2){
    width:50%;
    text-align:end;
}
div[data-fieldname="due_date"]{
    margin-top:0px;
    margin-bottom:0px;
}
div[data-fieldname="paid_amount"]{
    margin-top:0px;
    margin-bottom:0px;
    padding-top:5px;
    padding-bottom:5px;
    line-height:1;
}
div[data-fieldname="paid_amount"] > div > label {
    margin:0px;
}

.data-field{
    margin-top:0px;
    margin-bottom:0px;
}


/*changing css of erpnext elements*/
#taxes-section > .row > .col-xs-6:first-child {
    display: none;
}

#taxes-section > .row > .col-xs-6 {
    width: 100%;
}

#taxes-section > .row > .col-xs-6 > .row {
    padding-top:5px !important;
    padding-bottom:5px !important;
    line-height:1;
}

#taxes-section > .row > .col-xs-6 > .row > .col-xs-5 > label {
    margin:0px !important;
}

div[data-label="erpnext"] > div:nth-child(2) > .row > .col-xs-5 > label {
    margin:0px !important;
}

.column-break > .row:first-child {
    line-height:1;
    padding-bottom:5px !important;
}

div[data-fieldname="grand_total"] {
    margin-top:0px !important;
    margin-bottom:0px !important;
}
div[data-fieldname="rounded_total"] {
    margin-top:0px !important;
    margin-bottom:0px !important;
}
div[data-fieldname="in_words"] {
    margin-top:0px ;
    margin-bottom:0px;
    line-height:1;
    padding-top:5px;
}


/* last section of print format*/

