}

.amount__table td {
    border: 1px solid #c7c7c7;
    border-top: none;
    padding: 8px;
    vertical-align: top;
}

.amount-chargeable {
    text-align: left;
    font-weight: bold;
}

.eoe {
    text-align: right;
}

.amount-words {
    font-weight: bold;
}

.details div,
.amounts div {
    margin-bottom: 8px;
}

.net-amt {
    font-weight: bold;
}

.formula p {
    margin: 0;
}

.company-info p {
    margin: 0;
    line-height: 1.5;
}

/* Amount Table component ends here */

/* Bank Details Component starts here */

.bank-details-table {
    width: 100%;
    border-collapse: collapse;
    margin: 0;
}

.bank-details-title {
    font-weight: bold;
