    border: 1px solid #c7c7c7;
    border-top: none;
    text-align: left;
}

.bank-details-info,
.signatory {
    vertical-align: top;
    padding: 10px;
    border: 1px solid #c7c7c7;
    width: 50%;
}

.bank-details-info p,
.signatory p {
    margin: 5px 0;
}

.signatory {
    text-align: center;
}

.signature {
    display: block;
    margin: 10px auto;
