    margin: 5px 0;
}

.signatory {
    text-align: center;
}

.signature {
    display: block;
    margin: 10px auto;
    /* height: 50px; */
    width: 120px !important;
}

.authorized-signatory {
    font-weight: bold;
}

.footer-text {
    text-align: center;
    font-size: 12px;
    padding: 10px;
    border: 1px solid #c7c7c7;
    font-style: italic;
}

/* Bank Details Component ends here */

/* GST Component starts here */
.gst-summary {
    width: 100%;
    border-collapse: collapse;
    margin: 0;
}

.gst-summary th,
.gst-summary td {
    border: 1px solid #c7c7c7;
    padding: 8px;
    text-align: left;
}

.gst-summary th.summary-header {
    text-align: left;
    font-weight: bold;
    border-right: none;
}

.gst-summary thead th {
    border-bottom: 1px solid #c7c7c7;
}

.gst-summary tbody tr td:first-child {
    border-left: 1px solid #c7c7c7 !important;
}


/* GST Component ends here */
